import React from "react";
import {Grid, Box, MenuItem} from "@material-ui/core";
import { useStyles, titleTab } from 'app/c1utils/styles';
import C1InputField from "app/c1component/C1InputField";
import C1DateField from "../../../c1component/C1DateField";
import C1SelectField from "../../../c1component/C1SelectField";

const StudentDetails = ({
   inputData,
   errors,
   handleInputChange,
   viewType,
   handleDateChange
                       }) => {
    const title = titleTab();
    const classes = useStyles();
    let isDisabled = true;
    if (viewType === 'new')
        isDisabled = false;
    else if (viewType === 'edit')
        isDisabled = false;
    else if (viewType === 'view')
        isDisabled = false;

    const dropdownOptionsJobType = [
        { value: "M", desc: "Male" },
        { value: "F", desc: "Female" },
    ];
    return (
        <Grid container alignItems="flex-start" spacing={3} className={classes.gridContainer}>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="stuName"
                            disabled={false}
                            label="Student Name"
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.stuName}
                        />
                        <C1DateField
                            label="Date of Birth"
                            name="stuBirth"
                            type="date"
                            disabled={false}
                            required
                            onChange={handleDateChange}
                            value={inputData?.stuBirth}
                            disablePast={true}
                        />
                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1SelectField
                            name="stuGender"
                            label="Gender"
                            value={inputData?.stuGender}
                            onChange={(e) => handleInputChange(e)}
                            disabled={false}
                            isServer={false}
                            required={true}
                            optionsMenuItemArr={dropdownOptionsJobType?.map((item, ind) => (
                                <MenuItem value={item.value} key={ind}>
                                    {item.desc}
                                </MenuItem>
                            ))}
                            // error={errors["stuGender"] !== undefined}
                            // helperText={errors["stuGender"] || ""}
                        />
                        <C1InputField
                            name="stuPhone"
                            disabled={false}
                            label="Phone Number"
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.stuPhone}
                        />
                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="stuAddress"
                            disabled={false}
                            label="Address"
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.stuAddress}
                        />
                        <C1InputField
                            name="stuEmail"
                            disabled={false}
                            label="Email"
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.stuEmail}
                        />
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
        );
};

export default StudentDetails;
