import React from "react";
import { Grid, Box } from "@material-ui/core";
import { useStyles, titleTab } from 'app/c1utils/styles';
import C1InputField from "app/c1component/C1InputField";
import {APPLICATION_URL, CUSTOMER_URL, USERS_URL} from "../../../c1utils/const";
import C1SelectAutoCompleteField from "app/c1component/C1SelectAutoCompleteField";
import C1DateField from "../../../c1component/C1DateField";


const StudentDetails = ({
   inputData,
   errors,
   handleInputChange,
   handleSelectAutoCompleteChanged,
   viewType,
   handleDateChange
                       }) => {
    const title = titleTab();
    const classes = useStyles();
    let isDisabled = true;
    if (viewType === 'new')
        isDisabled = false;
    else if (viewType === 'edit')
        isDisabled = false;
    else if (viewType === 'view')
        isDisabled = false;
    return (
        <Grid container alignItems="flex-start" spacing={3} className={classes.gridContainer}>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="stuName"
                            disabled={false}
                            label="Student Name"
                            onChange={(e) => handleInputChange(e, 'stuName')}
                            value={inputData?.stuName}
                        />
                        <C1DateField
                            label="Date of Birth"
                            name="stuBirth"
                            type="date"
                            disabled={false}
                            required
                            onChange={handleDateChange}
                            value={inputData?.stuBirth}
                            disablePast={true}
                        />
                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1SelectField
                            name="jobSubType"
                            label={t("job:jobDetails.jobSubType")}
                            value={
                                inputData?.tckJob?.jobSubType
                            }
                            onChange={(e) => handleInputChange(e)}
                            disabled={isDisabled}
                            isServer={false}
                            required={true}
                            optionsMenuItemArr={dropdownOptionsJobType?.map((item, ind) => (
                                <MenuItem value={item.value} key={ind}>
                                    {item.desc}
                                </MenuItem>
                            ))}
                            error={errors["jobSubType"] !== undefined}
                            helperText={errors["jobSubType"] || ""}
                        />
                        <C1InputField
                            name="stuAddress"
                            disabled={false}
                            label="Phone Number"
                            onChange={(e) => handleInputChange(e, 'borTotalOwe')}
                            value={inputData?.stuAddress}
                        />
                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="stuAddress"
                            disabled={false}
                            label="Address"
                            onChange={(e) => handleInputChange(e, 'borTotalOwe')}
                            value={inputData?.stuAddress}
                        />
                        <C1InputField
                            name="stuEmail"
                            disabled={false}
                            label="Email"
                            onChange={(e) => handleInputChange(e, 'borTotalOwe')}
                            value={inputData?.stuAddress}
                        />
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
        );
};

export default StudentDetails;
