import React, { useEffect, useState } from "react";
import { useHistory } from "react-router-dom";

import { Box, Card, Divider, Grid, Tabs, Snackbar } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import { Formik, Form } from "formik";
import Footer from "app/MatxLayout/SharedCompoents/Footer";
import clsx from "clsx";
import { useTranslation } from "react-i18next";
import { MatxLoading } from "matx";
import C1Alert from "app/c1component/C1Alert";
import C1FormButtons from "app/c1component/C1FormButtons";
import mainLogo from "app/MatxLayout/Layout2/mainLogo.png";
import { registerTabs } from "app/c1utils/const";
import { isEmpty } from "app/c1utils/utility";
import { deepUpdateState } from "app/c1utils/stateUtils";
import useHttp from "app/c1hooks/http";
import UserDetail from "./UserDetail";
import { useParams } from "react-router-dom";
import {TabsWrapper} from "../../../portedicomponent/TabsWrapper";

const useStyles = makeStyles(({ palette, ...theme }) => ({
    cardHolder: {
        background: "#3C77D0",
        height: 979,
    },

    card: {
        width: "1200",
        borderRadius: 12,
        margin: "5rem",
    },

    formData: {
        height: "500vh"
    },

    root: {
        backgroundColor: "#3C77D0",
        borderColor: palette.divider,
        display: "table",
        height: "var(--topbar-height)",
        borderBottom: "1px solid transparent",
        paddingTop: "1rem",
        paddingBottom: "1rem",
        zIndex: 98,
        paddingLeft: "1.75rem",
        [theme.breakpoints.down("sm")]: {
            paddingLeft: "1rem",
        },
    },

    brandText: {
        color: palette.primary.contrastText,
    },

    button: {
        width: 140,
        height: 40,
        fontFamily: "sans-serif",
        fontWeight: "bolder",
        borderRadius: 12,
        backgroundColor: "white",
        color: "#3C77D0",
        margin: "10px 10px 20px 10px",
        border: "1px solid #3C77D0",
    },

    buttonBack: {
        width: 140,
        height: 40,
        fontFamily: "sans-serif",
        fontWeight: "bolder",
        borderRadius: 12,
        backgroundColor: "white",
        color: "#3C77D0",
        margin: "10px 10px 20px 10px",
        border: "1px solid #3C77D0",
        float: "right",
    },
    buttonSubmit: {
        float: "right",
    },

    title: {
        fontFamily: [
            "Roboto",
            "Helvetica",
            "Arial",
            "sans-serif",
            "Khmer OS Siemreap",
        ].join(","),
        fontWeight: "bolder",
        fontSize: "20px",
        margin: "10px 10px 20px 10px",
        float: "left",
    },
}));

const JwtRegister = (props) => {

    let { regId } = useParams();
    const { t } = useTranslation(["register", "common"]);
    const history = useHistory();
    const [tabIndex, setTabIndex] = useState(0);
    const [isSubmitSuccess, setSubmitSuccess] = useState(false);
    const [loading, setLoading] = useState(false);
    const classes = useStyles();

    const [enableTab, setEnableTab] = useState(false);

    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: "top",
        horizontal: "center",
        msg: "",
        severity: "success",
        redirectUrl: ''
    });

    //for server calls use sendRequest and responses will be stored in the corresponding deconstructed variables
    const { isLoading, res, validation, error, urlId, sendRequest } = useHttp();

    const [accnData, setAccnData] = useState();
    const [usrData, setUsrData] = useState();

    const [regDocs, setRegDocs] = useState([]);
    const [regDocIds, setRegDocIds] = useState([]);

    const [inputData, setInputData] = useState({});

    const [validationErrors, setValidationErrors] = useState([]);
    console.log("inputData", inputData)


    //for loading the supporting documents
    // useEffect(() => {
    //     if (regId) {
    //         setEnableTab(true);
    //         sendRequest(`/signup/${regId}`, "fetch", "get");
    //     } else {
    //         sendRequest("/api/register/new", "newSignUp", "get");
    //     }
    // }, [regId]);

    useEffect(() => {
        if (!isLoading && !error && res && !validation) {
            switch (urlId) {
                case "CREATE":
                    setLoading(false);
                    setInputData({ ...inputData, ...res.data });
                    setSnackBarState({
                        ...snackBarState,
                        open: true, msg: t("register"),
                        severity: "success", redirectUrl: `/session/signin`
                    });
                    setSubmitSuccess(true);
                    setValidationErrors({});
                    break;
                default:
                    break;
            }
        }

        if (validation) {
            //currently only tin is validated backend. TODO will have to change this to implement properly.
            const errorValidation = { ...validation };
            if (validation.hasOwnProperty("accnDetails")) {
                setTabIndex(0)
                errorValidation.accnDetails.accnCoyRegn && (
                    validation.accnDetails.accnCoyRegn = t("validations.accnDetails.accnCoyRegn")
                );
            }

            else if (validation.hasOwnProperty("coreUsr")) {
                if (urlId === 'submit')
                    setTabIndex(1);
                //ENABLE THE TAB
                setEnableTab(true);

            }
            else if (validation.hasOwnProperty("regDocs")) {
                if (urlId === 'submit') {
                    setTabIndex(2);
                    sendRequest("/api/register/suppDocs", "loadSuppDocs", "get");
                }

                //ENABLE THE TAB
                setEnableTab(true);
            }
            setValidationErrors({ ...validation });
            setLoading(false);
        }

        if (error) {
            setLoading(false);
        }
        // eslint-disable-next-line
    }, [isLoading, res, error, urlId, history]);

    const handleSnackbarClose = () => {
        setSnackBarState({ ...snackBarState, open: false });

        if (snackBarState && snackBarState.redirectUrl && snackBarState.severity === 'success') {
            //only redirect if it's success
            let url = snackBarState.redirectUrl;
            history.push(url);
        }
    };

    const handleTabChange = (e, value) => {
        setTabIndex(value);
        if (value === 2) {
            sendRequest("/api/register/suppDocs", "loadSuppDocs", "get");
        }
    };


    const handleSave = async () => {
        setLoading(true);
        sendRequest("/signup", "CREATE", "post", { ...inputData });
    };

    const handleValidate = () => {
        const errors = {
            regDocs: {},
        };

        //reset the error to proceed to submit
        if (isEmpty(errors.regDocs)) {
            return {};
        }

        return errors;
    };

    const handleInputChange = (e) => {
        const elName = e.target.name;
        setInputData({ ...inputData, ...deepUpdateState(inputData, elName, e.target.value) });
    };


    const handleAutoComplete = (e, name, value, reason) => {
        if (name.includes("accnDetails")) {
            setAccnData({ ...accnData, ...deepUpdateState(accnData, name, value?.value) });
        } else {
            setUsrData({ ...usrData, ...deepUpdateState(usrData, name, value?.value) });
        }
    };

    const handleDateChange = (name, e) => {
        setInputData({ ...inputData, [name]: e });
    };

    let snackBar;
    if (isSubmitSuccess) {
        const anchorOriginV = snackBarState.vertical;
        const anchorOriginH = snackBarState.horizontal;

        snackBar = (
            <Snackbar
                anchorOrigin={{ vertical: anchorOriginV, horizontal: anchorOriginH }}
                open={snackBarState.open}
                onClose={handleSnackbarClose}
                autoHideDuration={3000}
                key={anchorOriginV + anchorOriginH}
            >
                <C1Alert onClose={handleSnackbarClose} severity={snackBarState.severity}>
                    {snackBarState.msg}
                </C1Alert>
            </Snackbar>
        );
    }

    let formButtons = (
        <C1FormButtons
            options={{
                back: {
                    show: true,
                    eventHandler: () => history.push("/signin"),
                },
                save: {
                    show : true,
                    eventHandler: () => handleSave()
                }
            }}
        />
    );

    return (
        <div className="h-full-screen flex-column flex-grow">
            {snackBar}
            {loading && <MatxLoading />}
            <div className={clsx("relative w-full", classes.root)}>
                <div className="flex justify-between items-center h-full">
                    <div className="flex items-center h-full">
                        <img className="h-32" src={mainLogo} alt="" />
                        <span className={clsx("font-medium text-24 mx-4", classes.brandText)}>User Account Registration</span>
                    </div>
                </div>
            </div>
            <div className={classes.formData}>
            <Formik
                className={classes.formData}
                initialValues={inputData}
                onSubmit={(values, actions) => handleSave(values, actions, "submit")}
                enableReinitialize={true}
                values={inputData}
                validate={handleValidate}>
                {(props) => (
                    <Form noValidate={true}>
                        <Card className={classes.card}>
                            <div style={{ width: "100%", height: "70px" }}>
                                <Box display="flex" p={1}>
                                    <Box p={1} flexGrow={1}>
                                        <div className={classes.title}>{t("register.header")} </div>
                                    </Box>
                                    <Box p={1}>{formButtons}</Box>
                                </Box>
                            </div>
                            <Grid container>
                                <Grid item xs={12}>
                                    <Tabs
                                        className="mt-4"
                                        value={tabIndex}
                                        onChange={handleTabChange}
                                        indicatorColor="primary"
                                        textColor="primary" >

                                        {registerTabs.map((item, ind) => {
                                            return <TabsWrapper
                                                className="capitalize"
                                                value={ind}
                                                label={t(item.text)}
                                                key={ind}
                                                disabled={ind === 0 ? false : !enableTab}
                                                icon={item.icon}
                                            />
                                        })}
                                    </Tabs>
                                    <Divider className="mb-6" />
                                    {tabIndex === 0 && (
                                        <UserDetail
                                            inputData={inputData}
                                            errors={validationErrors}
                                            handleDateChange={handleDateChange}
                                            handleInputChange={handleInputChange}
                                            handleAutoComplete={handleAutoComplete}
                                            isSubmitting={loading || (['P', 'C', 'V'].includes(accnData?.accnDetails?.accnStatus))}
                                            locale={t}
                                        />
                                    )}
                                </Grid>
                            </Grid>
                        </Card>
                    </Form>
                )}
            </Formik>
            </div>
            <Footer />
        </div>
    );
};

export default JwtRegister;
