import React, { useEffect, useRef, useState } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const PrivateChatPopUpMessage = () => {
    const [username] = useState("user" + Math.floor(Math.random() * 1000));
    const [receiver, setReceiver] = useState("user123"); // ðŸ‘ˆ hardcoded or selected from list
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const stompClientRef = useRef(null);

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const stompClient = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("Connected");

                stompClient.subscribe(`/user/${username}/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages(prev => [...prev, msg]);
                });
            },
            onStompError: (frame) => {
                console.error("STOMP error", frame);
            }
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username]);

    const sendMessage = () => {
        if (input.trim() && stompClientRef.current && stompClientRef.current.connected) {
            stompClientRef.current.publish({
                destination: "/app/chat.sendPrivate",
                body: JSON.stringify({
                    sender: username,
                    receiver,
                    content: input,
                    type: "CHAT"
                })
            });
            setInput("");
        }
    };

    return (
        <div style={{ padding: 20 }}>
            <h2>Private Chat: {username} âžœ {receiver}</h2>
            <div style={{ height: 300, overflowY: "scroll", border: "1px solid #ccc", marginBottom: 10 }}>
                {messages.map((msg, idx) => (
                    <div key={idx}>
                        <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                ))}
            </div>
            <input value={input} onChange={(e) => setInput(e.target.value)} />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default PrivateChatPopUpMessage;
