import React, { useEffect, useRef, useState } from 'react';
import { Client } from '@stomp/stompjs';
import SockJS from 'sockjs-client';
import useAuth from "../../../hooks/useAuth";

const PrivateChatPopUpMessage = ({ popUpUsername }) => {
    const authUser = useAuth();
    const username = authUser?.user?.username;
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState('');
    const stompClientRef = useRef(null);

    useEffect(() => {
        if (!username) return;

        const socket = new SockJS('http://localhost:8080/ws');
        const stompClient = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log('✅ Connected');

                // Subscribe to private inbox
                stompClient.subscribe('/user/queue/messages', (message) => {
                    const msg = JSON.parse(message.body);
                    console.log('📩 Received:', msg);
                    setMessages((prev) => [...prev, msg]);
                });

                stompClient.publish({
                    destination: '/app/chat.sendPrivate',
                    body: JSON.stringify({ sender: username, content: input, type: 'CHAT', receiver: popUpUsername }),
                });
            },
            onStompError: (frame) => {
                console.error('❌ STOMP Error', frame);
            },
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username]);

    const sendMessage = () => {
        if (!input.trim() || !popUpUsername || !stompClientRef.current?.connected) return;

        const message = {
            sender: username,
            receiver: popUpUsername,
            content: input,
            type: 'CHAT',
        };

        stompClientRef.current.publish({
            destination: '/app/chat.sendPrivate',
            body: JSON.stringify(message),
        });

        setMessages((prev) => [...prev, message]); // optimistic update
        setInput('');
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Chat with {popUpUsername}</h2>
            <div
                style={{
                    height: '300px',
                    overflowY: 'auto',
                    border: '1px solid #ccc',
                    marginBottom: '10px',
                    padding: '10px',
                }}
            >
                {messages.map((msg, idx) => (
                    <div key={idx}>
                        <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                ))}
            </div>
            <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
                style={{ width: '80%' }}
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default PrivateChatPopUpMessage;
