import React, { useEffect, useState } from "react";

import { useTranslation } from "react-i18next"
import C1DataTable from 'app/c1component/C1DataTable';
import C1DataTableActions from 'app/c1component/C1DataTableActions';
import C1ListPanel from "app/c1component/C1ListPanel";
import useHttp from "app/c1hooks/http";
import ConfirmationDialog from "matx/components/ConfirmationDialog";
import history from "../../../../history";
import {formatDate} from "../../../c1utils/utility";
import useAuth from "../../../hooks/useAuth";

const StudentList = () => {

    const user = useAuth();
    console.log("user", user)
    const { t } = useTranslation(["student", "common"]);
    const { isLoading, res, error, urlId, sendRequest } = useHttp();
    const [confirm, setConfirm] = useState({ borId: null });
    const [open, setOpen] = useState(false);
    const [dtRefresh, setDtRefresh] = useState(false);
    const [borReturn, setBorReturn] = useState();
    const [loading, setLoading] = useState(false);
    const [confirmAction, setConfirmAction] = useState("");
    const API = '/api/v1/application/return/';
    useEffect(() => {
        if (!isLoading && !error && res) {
            if (urlId === "delete") {
                setOpen(false);
                setDtRefresh(true);
                setLoading(false);
            }else if(urlId === "getReturn"){
                //console.log("res.data", res.data);
                if (res.data === ''){
                    console.log("res.data", res.data);
                    history.push("/return/applicationReturn/new/" + borReturn);
                } else {
                    history.push("/return/applicationReturn/edit/" + res.data?.tapplication?.appID);
                }
            }
        }
    }, [isLoading, res, error, urlId]);

    const handleConfirmAction = (e) => {
        setDtRefresh(false);
        if (confirmAction === "delete") {
            handleDeleteHandler(e);
        }
    }

    const handleDeleteHandler = (e) => {
        if (confirm && !confirm.borId)
            return;
        setLoading(true);
        sendRequest("api/v1/library/application/bor" + confirm.borId, "delete", "delete", {})
    }
    const getReturnByBorrowId = (retID) => {
        setLoading(true);
        sendRequest(API + retID, "getReturn", "get", {})
        setBorReturn(retID);
    }

    const handleDeleteConfirm = (e, borId) => {
        setConfirmAction("delete");
        e.preventDefault();
        setConfirm({ ...confirm, borId: borId });
        setOpen(true);
    }

    const columns = [
        {
            name: "id",
            label: "ID",
        },
        {
            name: "fullName",
            label: "Name",
            options: {
                filter: true,
            },
        },
        {
            name: "dtOfBirth",
            label: "Date Of Birth",
            options: {
                filter: true,
                customBodyRender: (value) => {
                    return formatDate(value, true);
                }
            },
        },
        {
            name: "gender",
            label: "Gender",
            options: {
                filter: true,
            },
        },
        {
            name: "address",
            label: "Address",
            options: {
                filter: true,
            },
        },
        {
            name: "conNumber",
            label: "Phone Number",
            options: {
                filter: true
            },
        },
        {
            name: "email",
            label: "Email",
            options: {
                filter: false
            },
        },
        {
            name: "usrDtCreate",
            label: "Created Date",
            options: {
                filter: true,
                customBodyRender: (value) => {
                    return formatDate(value, true);
                }
            },
        },
        {
            name: "stuStatus",
            label: "Status",
            options: {
                filter: true,
            },
        },
        {
            name: "action",
            label: " ",
            options: {
                filter: false,
                display: true,
                viewColumns: false,
                customBodyRender: (value, tableMeta, updateValue) => {
                    return (<C1DataTableActions
                        editPath={`/student/applicationStudent/edit/${tableMeta.rowData[0]}`}
                        viewPath={`/student/applicationStudent/view/${tableMeta.rowData[0]}`} />);

                },
            },
        },
    ];

    return (<React.Fragment>
        {confirm && confirm.borId &&
            <ConfirmationDialog
                title="Student List"
                open={open}
                text={t("common:confirmMsgs.confirm", { borId: confirm.borId, action: confirmAction })}
                onYesClick={() => handleConfirmAction()}
                onConfirmDialogClose={() => setOpen(false)}
            />
        }

        <C1ListPanel
            routeSegments={[
                { name: "Application Student" },
            ]}>
            <C1DataTable 
                url={'/api/v1/users'}
                columns={columns}
                title={"Student List"}
                defaultOrder="usrDtCreate"
                isServer={true}
                isShowDownload={false}
                isShowPrint={false}
                isRowSelectable={false}
                isShowToolbar
                isRefresh={dtRefresh}
                filterBy={[
                    { attribute: "username" , value : "brosna"}
                ]}
                // defaultOrderDirection={"asc"}
                showAdd={{
                    path: "/student/applicationStudent/new/0"
                }}
            />
        </C1ListPanel>
    </React.Fragment>
    );
};

export default StudentList;
