import React from "react";
import {Grid, Box, MenuItem} from "@material-ui/core";
import { useStyles, titleTab } from 'app/c1utils/styles';
import C1InputField from "app/c1component/C1InputField";
import C1DateField from "../../../c1component/C1DateField";
import C1SelectField from "../../../c1component/C1SelectField";
import {useTranslation} from "react-i18next";

const UsersDetails = ({
   inputData,
   errors,
   handleInputChange,
   viewType,
   handleDateChange
                       }) => {
    const title = titleTab();
    const classes = useStyles();

    const { t } = useTranslation(["user"])

    const dropdownOptionsJobType = [
        { value: "M", desc: "Male" },
        { value: "F", desc: "Female" },
    ];
    return (
        <Grid container alignItems="flex-start" spacing={3} className={classes.gridContainer}>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="username"
                            disabled={viewType === 'view' && viewType === 'edit'}
                            label={t("user.details.username")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.username ?? ""}
                        />
                        <C1InputField
                            name="email"
                            disabled={viewType === 'view' && viewType === 'edit'}
                            label={t("user.details.email")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.email}
                        />
                        <C1InputField
                            name="conNumber"
                            disabled={viewType === 'view'}
                            label={t("user.details.conNumber")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.conNumber}
                        />

                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="firstname"
                            disabled={viewType === 'view'}
                            label={t("user.details.firstName")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.firstname}
                        />
                        <C1SelectField
                            name="gender"
                            label={t("user.details.gender")}
                            value={inputData?.gender ?? ""}
                            onChange={(e) => handleInputChange(e)}
                            disabled={viewType === 'view'}
                            isServer={false}
                            required={true}
                            optionsMenuItemArr={dropdownOptionsJobType?.map((item, ind) => (
                                <MenuItem value={item.value} key={ind}>
                                    {item.desc}
                                </MenuItem>
                            ))}
                            // error={errors["stuGender"] !== undefined}
                            // helperText={errors["stuGender"] || ""}
                        />
                        <C1InputField
                            name="address"
                            disabled={viewType === 'view'}
                            label={t("user.details.address")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.address}
                        />
                    </Grid>
                </Grid>
            </Grid>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            name="lastName"
                            disabled={viewType === 'view'}
                            label={t("user.details.lastName")}
                            onChange={(e) => handleInputChange(e)}
                            value={inputData?.lastname}
                        />
                        <C1DateField
                            label={t("user.details.dateOfBirth")}
                            name="dtOfBirth"
                            type="date"
                            disabled={viewType === 'view'}
                            required
                            onChange={handleDateChange}
                            value={inputData?.dtOfBirth}
                            disablePast={true}
                        />

                    </Grid>
                </Grid>
            </Grid>
        </Grid>
        );
};

export default UsersDetails;
