import React, {useEffect, useState, useCallback, useRef} from "react";
import { useTranslation } from "react-i18next";
import Grid from "@material-ui/core/Grid";
import { Box, Button, Snackbar, Paper, Typography } from "@material-ui/core";
import { useStyles } from "../../../c1utils/styles";
import C1Alert from "../../../c1component/C1Alert";
import SockJS from "sockjs-client";
import C1InputField from "../../../c1component/C1InputField";
import useHttp from "../../../c1hooks/http";

const UserPopUpMessage = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const ws = useRef(null);
    const { isLoading, isFormSubmission, res, validation, error, urlId, sendRequest } = useHttp();
    const [inputData, setInputData] = useState({});
    const classes = useStyles();
    const { t } = useTranslation();
    const [snackBarOptions, setSnackBarOptions] = useState(defaultSnackbarValue);
    const [errors, setErrors] = useState({});
    const [loading, setLoading] = useState(false);
    const [openDeletePopupAction, setOpenDeletePopupAction] = useState(false);
    const [refresh, setRefresh] = useState(false);

    useEffect(() => {
        ws.current = new WebSocket("ws://localhost:8080/chat");

        ws.current.onmessage = (event) => {
            setMessages(prev => [...prev, event.data]);
        };

        ws.current.onopen = () => {
            console.log("Connected to WebSocket");
        };

        ws.current.onclose = () => {
            console.log("Disconnected");
        };

        return () => {
            ws.current.close();
        };
    }, []);
    useEffect(() => {
        setSnackBarOptions(defaultSnackbarValue);
        if (!isLoading && !error && res && !validation) {
            setLoading(false);
            switch (urlId) {
                case "doFetch":{
                    setInputData(res.data?.data);
                    setData(res.data)
                    break;
                }
                case "doNew": {
                    setInputData(res.data.data);
                    setData(res.data.data)
                    break;
                }
                case "CREATE": {
                    setInputData(res.data);
                    setSnackBarOptions({
                        ...snackBarOptions,
                        success: true,
                        successMsg: `Application Student has been created.`,
                        redirectPath: `/student/applicationStudent/list`
                    });
                    break;
                }
                default: break;
            }
            setRefresh(true);
            setOpenDeletePopupAction(false);
        }
        if (validation) {
            setErrors(validation);
            setLoading(false);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [isLoading, isFormSubmission, validation, error]);
    const sendMessage = () => {
        sendRequest(`http://localhost:8080/chat`, "CREATE", "post", { ...inputData });
    };

    return (
        <div style={{ padding: 20 }}>
            <h2>WebSocket Chat</h2>
            <div style={{ border: "1px solid gray", padding: 10, height: 200, overflowY: "scroll" }}>
                {messages.map((msg, i) => <div key={i}>{msg}</div>)}
            </div>
            <input
                value={input}
                onChange={e => setInput(e.target.value)}
                onKeyDown={e => e.key === "Enter" && sendMessage()}
                placeholder="Type message..."
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default UserPopUpMessage;
