import React from "react";
import Grid from "@material-ui/core/Grid";
import { useStyles } from "app/c1utils/styles";
import C1InputField from "app/c1component/C1InputField";
import { getValue } from "app/c1utils/utility";
import { MST_CTRY_URL } from "app/c1utils/const";
import C1SelectAutoCompleteField from "app/c1component/C1SelectAutoCompleteField";

const UserDetail = ({
    inputData,
    handleInputChange,
    handleAutoComplete,
    errors,
    isSubmitting,
    locale }) => {

    const classes = useStyles();

    return (
        <Grid container alignItems="flex-start" spacing={3} className={classes.gridContainer}>
            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            label={locale("userDetails.username")}
                            name="username"
                            disabled={isSubmitting}
                            required
                            inputProps={{
                                maxLength: 35,
                                placeholder: locale("userDetails.enterYourUsername")
                            }}
                            onChange={handleInputChange}
                            value={getValue(inputData?.username)}
                            error={errors && errors['usrName'] !== undefined}
                            helperText={(errors && errors['usrName']) || ''} />

                        <C1InputField
                            label={locale("userDetails.email")}
                            name="email"
                            disabled={isSubmitting}
                            required
                            inputProps={{
                                maxLength: 35,
                                placeholder: locale("userDetails.enterYourEmail")
                            }}
                            onChange={handleInputChange}
                            value={getValue(inputData?.email)}
                            error={errors && errors['email'] !== undefined}
                            helperText={(errors && errors['email']) || ''} />


                    </Grid>
                </Grid>
            </Grid>

            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12}>
                        <C1InputField
                            label={locale("userDetails.firstName")}
                            name="firstname"
                            disabled={isSubmitting}
                            required
                            inputProps={{
                                placeholder: locale("userDetails.enterYourFirstName")
                            }}
                            onChange={handleInputChange}
                            value={getValue(inputData?.usrContact?.contactTel)}
                            error={errors.coreUsr && errors.coreUsr['usrContact.contactTel'] !== undefined}
                            helperText={(errors.coreUsr && errors.coreUsr['usrContact.contactTel']) || ''} />
                        <C1InputField
                            label={locale("userDetails.password")}
                            name="usrAddr.addrProv"
                            disabled={isSubmitting}
                            inputProps={{
                                maxLength: 15,
                                placeholder: locale("userDetails.enterYourPassword")
                            }}
                            required
                            onChange={handleInputChange}
                            value={getValue(inputData?.usrAddr?.addrProv)}
                            error={errors.coreUsr && errors.coreUsr['usrAddr.addrProv'] !== undefined}
                            helperText={(errors.coreUsr && errors.coreUsr['usrAddr.addrProv']) || ''} />
                    </Grid>
                </Grid>
            </Grid>

            <Grid item lg={4} md={6} xs={12} >
                <Grid container alignItems="center" spacing={3} className={classes.gridContainer}>
                    <Grid item xs={12} >
                        <C1InputField
                            label={locale("userDetails.lastName")}
                            name="usrAddr.addrProv"
                            disabled={isSubmitting}
                            inputProps={{
                                placeholder: locale("userDetails.enterYourLastName")
                            }}
                            required
                            onChange={handleInputChange}
                            value={getValue(inputData?.usrAddr?.addrProv)}
                            error={errors.coreUsr && errors.coreUsr['usrAddr.addrProv'] !== undefined}
                            helperText={(errors.coreUsr && errors.coreUsr['usrAddr.addrProv']) || ''} />

                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    );
};
export default UserDetail;
