import React, { useEffect, useRef, useState, useCallback } from "react";
import { useTranslation } from "react-i18next";
import Grid from "@material-ui/core/Grid";
import { Box, Button, Snackbar } from "@material-ui/core";
import { useStyles } from "../../../c1utils/styles";
import C1TextArea from "../../../c1component/C1TextArea";
import C1Alert from "../../../c1component/C1Alert";

const UserPopUpMessage = ({ handleOnClose }) => {
    const classes = useStyles();
    const { t } = useTranslation(["job"]);

    const socketRef = useRef(null);

    const [message, setMessage] = useState("");
    const [socketConnected, setSocketConnected] = useState(false);
    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: "top",
        horizontal: "center",
        msg: "",
        severity: "success",
    });

    // Snackbar close
    const handleCloseSnackBar = () => {
        setSnackBarState(prev => ({ ...prev, open: false }));
    };

    const [messages, setMessages] = useState([]);
    const [ws, setWs] = useState(null);

    useEffect(() => {
        // Establish WebSocket connection when component mounts
        const socket = new WebSocket('ws://localhost:8080/ws');

        socket.onopen = () => {
            console.log('WebSocket connected');
            setWs(socket);
        };

        socket.onmessage = (event) => {
            const newMessage = JSON.parse(event.data);
            console.log('Message received:', newMessage);

            // Update state with the new message
            setMessages(prevMessages => [...prevMessages, newMessage]);
        };

        socket.onclose = () => {
            console.log('WebSocket disconnected');
        };

        socket.onerror = (error) => {
            console.error('WebSocket error:', error);
        };

        // Clean up on unmount
        return () => {
            if (socket) {
                socket.close();
            }
        };
    }, []);

    const sendMessage = (message) => {
        if (ws && ws.readyState === WebSocket.OPEN) {
            ws.send(JSON.stringify(message));
        }
    };
    // Connect WebSocket
    useEffect(() => {
        // Establish WebSocket connection when component mounts
        const socket = new WebSocket('ws://localhost:8080/ws');
        socketRef.current.onopen = () => {
            console.log("WebSocket connected");
            setSocketConnected(true);
        };

        socketRef.current.onerror = (error) => {
            console.error("WebSocket error", error);
            setSnackBarState({
                open: true,
                msg: "WebSocket connection error!",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
        };

        socketRef.current.onclose = () => {
            console.log("WebSocket closed");
            setSocketConnected(false);
        };

        // Optional: listen to incoming message
        socketRef.current.onmessage = (event) => {
            const response = JSON.parse(event.data);
            console.log("Received message:", response);
        };

        // Cleanup
        return () => {
            if (socketRef.current && socketRef.current.readyState === WebSocket.OPEN) {
                socketRef.current.close();
            }
        };
    }, []);

    // Send message
    const handleSendMessage = useCallback(() => {
        if (!message || !socketConnected) {
            setSnackBarState({
                open: true,
                msg: "Message is empty or WebSocket is not connected.",
                severity: "warning",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        const payload = {
            type: "MESSAGE",
            content: message,
            timestamp: new Date().toISOString(),
        };

        socketRef.current.send(JSON.stringify(payload));
        setMessage(""); // Clear message box

        setSnackBarState({
            open: true,
            msg: "Message sent!",
            severity: "success",
            vertical: "top",
            horizontal: "center",
        });
    }, [message, socketConnected]);

    // Enter key to send
    const handleKeyPress = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <>
            <Grid container spacing={2} className={classes.gridContainer}>
                <Grid item xs={12}>
                    <C1TextArea
                        label={t("job:Enter your message")}
                        name="message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        rows={4}
                        fullWidth
                    />
                </Grid>

                <Grid item xs={12} style={{ display: "flex", justifyContent: "flex-end" }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSendMessage}
                        disabled={!socketConnected}
                        style={{ marginRight: "20px", color: "#fff" }}
                    >
                        {t("job:Send")}
                    </Button>

                    <Button
                        variant="outlined"
                        color="secondary"
                        onClick={handleOnClose}
                    >
                        {t("job:Close")}
                    </Button>
                </Grid>
            </Grid>

            <Snackbar
                anchorOrigin={{
                    vertical: snackBarState.vertical,
                    horizontal: snackBarState.horizontal,
                }}
                open={snackBarState.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackBar}
                key={snackBarState.vertical + snackBarState.horizontal}
            >
                <C1Alert onClose={handleCloseSnackBar} severity={snackBarState.severity}>
                    {snackBarState.msg}
                </C1Alert>
            </Snackbar>
        </>
    );
};

export default UserPopUpMessage;
