import React, { useEffect, useState, useCallback } from "react";
import { useTranslation } from "react-i18next";
import Grid from "@material-ui/core/Grid";
import { Box, Button, Snackbar, Paper, Typography } from "@material-ui/core";
import { useStyles } from "../../../c1utils/styles";
import C1Alert from "../../../c1component/C1Alert";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";
import C1InputField from "../../../c1component/C1InputField";

const UserPopUpMessage = ({ handleOnClose }) => {
    const { t } = useTranslation(["job"]);
    const classes = useStyles();

    const [stompClient, setStompClient] = useState(null);
    const [message, setMessage] = useState("");
    const [socketConnected, setSocketConnected] = useState(false);
    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: "top",
        horizontal: "center",
        msg: "",
        severity: "success",
    });
    const [chatHistory, setChatHistory] = useState([]);

    // Snackbar close
    const handleCloseSnackBar = () => {
        setSnackBarState((prev) => ({ ...prev, open: false }));
    };

    // Fetch previous messages from backend
    useEffect(() => {
        fetch("http://localhost:8080/messages")
            .then((res) => res.json())
            .then((data) => {
                setChatHistory(data);
            })
            .catch((err) => console.error("Failed to fetch messages", err));

        console.log("WebSocket connection initializing...");
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("Connected to WebSocket");
                setSocketConnected(true);

                client.subscribe("/topic/public", (message) => {
                    const payload = JSON.parse(message.body);
                    console.log("Received:", payload);

                    setSnackBarState({
                        open: true,
                        msg: `New message: ${payload.content}`,
                        severity: "info",
                        vertical: "top",
                        horizontal: "center",
                    });

                    // Add incoming message to chat history
                    setChatHistory((prevHistory) => [...prevHistory, payload]);
                });

                client.publish({
                    destination: "/app/chat.addUser",
                    body: JSON.stringify({ sender: "YourName", type: "JOIN" }),
                });
            },
            onDisconnect: () => {
                console.log("Disconnected from WebSocket");
                setSocketConnected(false);
            },
            onStompError: (frame) => {
                console.error("STOMP Error", frame);
            },
        });

        client.activate();
        setStompClient(client);

        return () => {
            client.deactivate();
        };
    }, []);

    const handleSendMessage = useCallback(() => {
        console.log("Message:", message);
        if (!message || !socketConnected || !stompClient) {
            setSnackBarState({
                open: true,
                msg: "Message is empty or WebSocket is not connected.",
                severity: "warning",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        const payload = {
            sender: "YourName", // Replace with dynamic user if needed
            content: message,
            type: "MESSAGE",
        };

        stompClient.publish({
            destination: "/app/chat.sendMessage",
            body: JSON.stringify(payload),
        });

        setMessage(""); // Clear the input field

        setSnackBarState({
            open: true,
            msg: "Message sent!",
            severity: "success",
            vertical: "top",
            horizontal: "center",
        });
    }, [message, socketConnected, stompClient]);

    const handleKeyPress = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <>
            <Grid container spacing={2} className={classes.gridContainer}>
                <Grid item xs={12}>
                    {/* Chat History */}
                    <Paper style={{ maxHeight: 300, overflowY: "scroll" }}>
                        <Box p={2}>
                            {chatHistory.map((msg, idx) => (
                                <Typography key={idx} variant="body1">
                                    <strong>{msg?.sender}: </strong>{msg.content}
                                </Typography>
                            ))}
                        </Box>
                    </Paper>
                </Grid>

                <Grid item xs={12}>
                    <C1InputField
                        label={t("job:Enter your message")}
                        name="message"
                        value={message}
                        onChange={(e) => setMessage(e.target.value)}
                        onKeyPress={handleKeyPress}
                        rows={4}
                        fullWidth
                    />
                </Grid>

                <Grid item xs={12} style={{ display: "flex", justifyContent: "flex-end" }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSendMessage}
                        disabled={!socketConnected || !message} // Disable button when not connected or message is empty
                        style={{ marginRight: "20px", color: "#fff" }}
                    >
                        {t("job:Send")}
                    </Button>

                    <Button
                        variant="outlined"
                        color="secondary"
                        onClick={handleOnClose}
                    >
                        {t("job:Close")}
                    </Button>
                </Grid>
            </Grid>

            <Snackbar
                anchorOrigin={{
                    vertical: snackBarState.vertical,
                    horizontal: snackBarState.horizontal,
                }}
                open={snackBarState.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackBar}
                key={snackBarState.vertical + snackBarState.horizontal}
            >
                <C1Alert onClose={handleCloseSnackBar} severity={snackBarState.severity}>
                    {snackBarState.msg}
                </C1Alert>
            </Snackbar>
        </>
    );
};

export default UserPopUpMessage;
