import React, { useState, useEffect, useRef } from "react";
import { Client } from "@stomp/stompjs";
import SockJS from "sockjs-client";

const PrivateChatPopUpMessage = () => {
    const [username] = useState("user" + Math.floor(Math.random() * 1000)); // Example username
    const [receiver, setReceiver] = useState("user123"); // Set this dynamically based on your app
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const [connected, setConnected] = useState(false); // Track connection status
    const stompClientRef = useRef(null);

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const stompClient = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("Connected");

                // Subscribe to the user-specific queue for private messages
                stompClient.subscribe(`/user/${username}/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages(prev => [...prev, msg]);
                });

                stompClient.publish({
                    destination: "/app/chat.addUser",
                    body: JSON.stringify({ sender: username, type: "JOIN" }),
                });
            },
            onStompError: (frame) => {
                console.error("STOMP error", frame);
            }
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username]);


    const sendMessage = () => {
        if (input.trim() && connected) {
            if (stompClientRef.current) {
                stompClientRef.current.publish({
                    destination: "/app/chat.sendPrivate",
                    body: JSON.stringify({
                        sender: username,
                        receiver,
                        content: input,
                        type: "CHAT"
                    })
                });
                setInput(""); // Clear the input field
            }
        } else {
            console.error("STOMP client is not connected. Cannot send message.");
        }
    };

    return (
        <div style={{ padding: 20 }}>
            <h2>Private Chat: {username} âžœ {receiver}</h2>
            <div style={{ height: 300, overflowY: "scroll", border: "1px solid #ccc", marginBottom: 10 }}>
                {messages.map((msg, idx) => (
                    <div key={idx}>
                        <strong>{msg.sender}:</strong> {msg.content}
                    </div>
                ))}
            </div>
            <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                placeholder="Type a message"
                disabled={!connected} // Disable input if not connected
            />
            <button onClick={sendMessage} disabled={!connected}>Send</button>
            {!connected && <p>Connecting to chat...</p>} {/* Optional: Show message when not connected */}
        </div>
    );
};

export default PrivateChatPopUpMessage;
