import React, { useEffect, useState } from "react";

import { useTranslation } from "react-i18next"
import C1DataTable from 'app/c1component/C1DataTable';
import C1DataTableActions from 'app/c1component/C1DataTableActions';
import C1ListPanel from "app/c1component/C1ListPanel";
import useHttp from "app/c1hooks/http";
import ConfirmationDialog from "matx/components/ConfirmationDialog";
import {formatDate} from "../../../c1utils/utility";
import useAuth from "../../../hooks/useAuth";
import C1PopUp from "../../../c1component/C1PopUp";
import UserPopUpMessage from "../messagePopUp/PrivateChatPopUpMessage";

const UsersList = () => {

    const auth = useAuth();
    const { t } = useTranslation(["student", "common"]);
    const { isLoading, res, error, urlId, sendRequest } = useHttp();
    const [confirm, setConfirm] = useState({ borId: null });
    const [open, setOpen] = useState(false);
    const [dtRefresh, setDtRefresh] = useState(false);
    const [loading, setLoading] = useState(false);
    const [confirmAction, setConfirmAction] = useState("");
    const [openPopUp, setOpenPopUp] = useState(false);
    const [errors, setErrors] = useState({});
    const [deleteId, setDeleteId] = useState("");
    const [action, setAction] = useState(null);
    const [formAction, setFormAction] = useState("");
    const [isPopUpId, setPopupId] = useState("");
    const [userSender, setSender] = useState("");
    const [isRefresh, setRefresh] = useState(false);
    const [message, setMessage] = useState([]);


    // useEffect(() => {
    //     setLoading(false)
    //     if (!isReceiver) return; // Avoid calling when receiver is not valid
    //     sendRequest(`/receiver/${isReceiver}`, 'FETCH', 'GET', null);
    // }, [isReceiver]);

    //
    // useEffect(() => {
    //     if (!isLoading && !error && res) {
    //         setLoading(false);
    //         switch (urlId) {
    //             case "FETCH":{
    //                 setMessage(res.data);
    //                 console.log("data", res.data?.[0]);
    //                 setSender(res.data?.[0]?.userSender?.id);
    //                 if (auth?.user?.roles?.[0]?.name === "ROLE_STUDENT") {
    //                     setReceiver(res.data?.[0]?.userReceiver?.id);
    //                 }
    //                 break;
    //             }
    //             default: break;
    //         }
    //         setRefresh(true);
    //     }
    //     // eslint-disable-next-line react-hooks/exhaustive-deps
    // }, [isLoading, error]);

    const handleConfirmAction = (e) => {
        setDtRefresh(false);
        if (confirmAction === "delete") {
            handleDeleteHandler(e);
        }
    }

    const handleDeleteHandler = (e) => {
        if (confirm && !confirm.borId)
            return;
        setLoading(true);
        sendRequest("api/v1/library/application/bor" + confirm.borId, "delete", "delete", {})
    }

    const handleDeleteConfirm = (e, borId) => {
        setConfirmAction("delete");
        e.preventDefault();
        setConfirm({ ...confirm, borId: borId });
        setOpen(true);
    }

    const columns = [
        {
            name: "id",
            label: "ID",
        },
        {
            name: "fullName",
            label: "Name",
            options: {
                filter: true,
            },
        },
        {
            name: "dtOfBirth",
            label: "Date Of Birth",
            options: {
                filter: true,
                customBodyRender: (value) => {
                    return formatDate(value, true);
                }
            },
        },
        {
            name: "gender",
            label: "Gender",
            options: {
                filter: true,
            },
        },
        {
            name: "address",
            label: "Address",
            options: {
                filter: true,
            },
        },
        {
            name: "conNumber",
            label: "Phone Number",
            options: {
                filter: true
            },
        },
        {
            name: "email",
            label: "Email",
            options: {
                filter: false
            },
        },
        {
            name: "usrDtCreate",
            label: "Created Date",
            options: {
                filter: true,
                customBodyRender: (value) => {
                    return formatDate(value, true);
                }
            },
        },
        {
            name: "status",
            label: "Status",
            options: {
                filter: true,
            },
        },
        {
            name: "action",
            label: " ",
            options: {
                filter: false,
                display: true,
                viewColumns: false,
                customBodyRender: (value, tableMeta, updateValue) => {
                    let remarkCount = 5;
                    return (<C1DataTableActions
                        editPath={`/student/applicationStudent/edit/${tableMeta.rowData[0]}`}
                        viewPath={`/student/applicationStudent/view/${tableMeta.rowData[0]}`}
                        // remarkPath={(e) => handleMessagePopUp(e)}
                        remarkPath={
                            remarkCount && remarkCount > 0
                                ? {onClick: (e) => handleMessagePopUp(e, tableMeta.rowData[0]),
                                    count: remarkCount}
                                : null
                        }
                    />);
                },
            },
        },
    ];
    const handleOnClose = (e) => {
        setOpenPopUp(false)
    }
    const handleMessagePopUp = (e, id) => {
        console.log("id", id)
        setLoading(false)
        setPopupId(id)
        e.stopPropagation(); // Optional, to avoid row clicks
        setFormAction("remark");
        setAction("remark");
        setOpenPopUp(true);
        setErrors({});
    };
    // const handleMessagePopUp = (e) => {
    //     console.log("hello");
    //     setFormAction(action)
    //     setAction(action);
    //     setOpenPopUp(true);
    //     setErrors({});
    // };

    return (<React.Fragment>
        {confirm && confirm.borId &&
            <ConfirmationDialog
                title="Student List"
                open={open}
                text={t("common:confirmMsgs.confirm", { borId: confirm.borId, action: confirmAction })}
                onYesClick={() => handleConfirmAction()}
                onConfirmDialogClose={() => setOpen(false)}
            />
        }
            <C1PopUp
                title={"Message Alert"}
                openPopUp={openPopUp}
                setOpenPopUp={setOpenPopUp}
                maxWidth="lg"
                maxHeight="500px"
                overflowY="auto"
                flex-wrap="none"
                customStyles={{
                    backgroundColor: "rgba(244,244,244,0.11)",
                    borderRadius: "12px",
                    boxShadow: "0 4px 10px rgba(0, 0, 0, 0.2)",
                    bottom: "100px",
                }}
                disableCloseButton={true}
            >
                <UserPopUpMessage
                    auth={auth}
                    sender={userSender}
                    isPopUpId={isPopUpId}
                    message={message}
                    errors={errors}
                    handleOnClose={handleOnClose}
                />
            </C1PopUp>
        <C1ListPanel
            routeSegments={[
                { name: "Application Student" },
            ]}>
            <C1DataTable 
                url={'/api/v1/users'}
                columns={columns}
                title={"Student List"}
                defaultOrder="usrDtCreate"
                isServer={true}
                isShowDownload={false}
                isShowPrint={false}
                isRowSelectable={false}
                isShowToolbar
                isRefresh={dtRefresh}
                filterBy={[
                    { attribute: "username" , value : auth?.user?.username}
                ]}
                // defaultOrderDirection={"asc"}
                showAdd={{
                    path: "/student/applicationStudent/new/0"
                }}
            />
        </C1ListPanel>
    </React.Fragment>
    );
};

export default UsersList;
