import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";

const UserPopUpMessage = () => {
    const [messages, setMessages] = useState([]);
    const stompClientRef = useRef(null);
    const [input, setInput] = useState("");

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("Connected!");

                client.subscribe("/topic/public", (message) => {
                    const msgBody = JSON.parse(message.body);
                    setMessages((prev) => [...prev, msgBody.content]);
                });
            },
            onStompError: (frame) => {
                console.error("Broker error:", frame.headers["message"]);
                console.error("Details:", frame.body);
            },
        });

        client.activate();
        stompClientRef.current = client;

        return () => {
            client.deactivate();
        };
    }, []);

    const sendMessage = () => {
        if (stompClientRef.current && input) {
            const chatMessage = {
                sender: "User", // change to dynamic username if needed
                content: input,
                type: "CHAT",
            };
            stompClientRef.current.publish({
                destination: "/app/chat.sendMessage",
                body: JSON.stringify(chatMessage),
            });
            setInput("");
        }
    };

    return (
        <div style={{ padding: 20 }}>
            <h2>WebSocket Chat</h2>
            <div style={{ border: "1px solid gray", padding: 10, height: 200, overflowY: "scroll" }}>
                {messages.map((msg, index) => (
                    <div key={index}>{msg}</div>
                ))}
            </div>
            <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                placeholder="Type message..."
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default UserPopUpMessage;
