import React, { useEffect, useRef, useState, useCallback } from "react";
import { useTranslation } from "react-i18next";
import Grid from "@material-ui/core/Grid";
import { Box, Button, Snackbar } from "@material-ui/core";
import { useStyles } from "../../../c1utils/styles";
import C1TextArea from "../../../c1component/C1TextArea";
import C1Alert from "../../../c1component/C1Alert";
import { getValue } from "../../../c1utils/utility"
import useAuth from "../../../hooks/useAuth";
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';
import SwapHorizIcon from '@material-ui/icons/SwapHoriz';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import useHttp from "../../../c1hooks/http";

const UserPopUpMessage = ({ receiver,
                              handleOnClose
                          }) => {
    console.log("receiver", receiver)
    const auth = useAuth();

    const sender = auth?.user?.id;
    console.log("sender", sender)
    console.log("auth", auth)
    const classes = useStyles();
    const { t } = useTranslation(["job"]);
    const { isLoading, isFormSubmission, res, validation, error, urlId, sendRequest } = useHttp();
    const socketRef = useRef(null);
    const [isRefresh, setRefresh] = useState(false);
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});
    const [messageInput, setMessageInput] = useState({
        message: "",
        receiver: receiver, // assume this is passed as a prop or defined somewhere
        type: "CHAT"
    });
    const [messages, setMessages] = useState([]);
    const [socketConnected, setSocketConnected] = useState(false);

    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: "top",
        horizontal: "center",
        msg: "",
        severity: "success",
    });
    console.log("messages", messages)

    useEffect(() => {
        sendRequest(`/${sender}/${receiver}`, 'FETCH', 'GET', null);
        // eslint-disable-next-line
    }, [receiver, sender]);

    useEffect(() => {
        if (!isLoading && !error && res && !validation) {
            setLoading(false);
            switch (urlId) {
                case "FETCH":{
                    console.log("res.data?.data", res.data)
                    setMessages(res.data);
                    break;
                }
                default: break;
            }
            setRefresh(true);
        }
        if (validation) {
            setErrors(validation);
            setLoading(false);
        }
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [isLoading, isFormSubmission, validation, error]);
    const handleCloseSnackBar = () => {
        setSnackBarState(prev => ({ ...prev, open: false }));
    };

    // Simplified deepUpdateState function
    const deepUpdateState = (state, path, value) => {
        const [key] = path.split('.');
        return {
            ...state,
            [key]: value
        };
    };

    const handleInputChange = (e) => {
        console.log("eee", e)
        const { name, value } = e.target;
        console.log("before:", messageInput);
        setMessageInput((prev) => {
            const newState = {
                ...prev,
                [name]: value,
            };
            console.log("after:", newState);
            return newState;
        });
    };

    console.log("inputMessage", messageInput)
    // WebSocket connection management (same as before)
    useEffect(() => {
        const socket = new WebSocket('ws://localhost:8080/ws');
        socketRef.current = socket;

        const handleOpen = () => {
            console.log('WebSocket connected');
            setSocketConnected(true);
        };

        const handleError = (error) => {
            console.error("WebSocket error", error);
            setSocketConnected(false);
            setSnackBarState({
                open: true,
                msg: "Connection error. Trying to reconnect...",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
        };

        const handleClose = () => {
            console.log("WebSocket closed");
            setSocketConnected(false);
        };

        const handleMessage = (event) => {
            try {
                const response = JSON.parse(event.data);
                console.log("Received message:", response);
                setMessages(prev => [...prev, response]);
            } catch (error) {
                console.error("Error parsing message:", error);
            }
        };

        socket.addEventListener('open', handleOpen);
        socket.addEventListener('error', handleError);
        socket.addEventListener('close', handleClose);
        socket.addEventListener('message', handleMessage);

        return () => {
            socket.removeEventListener('open', handleOpen);
            socket.removeEventListener('error', handleError);
            socket.removeEventListener('close', handleClose);
            socket.removeEventListener('message', handleMessage);

            if (socket.readyState === WebSocket.OPEN) {
                socket.close();
            }
        };
    }, []);

    const handleSendMessage = useCallback(() => {
        if (!messageInput.message?.trim()) {
            setSnackBarState({
                open: true,
                msg: "Message cannot be empty",
                severity: "warning",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        if (!socketConnected) {
            setSnackBarState({
                open: true,
                msg: "Not connected to server",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        const payload = {
            type: "MESSAGE",
            content: messageInput.message,
            sender: auth?.user?.id,
            receiver: receiver,
            timestamp: new Date().toISOString(),
        };

        try {
            socketRef.current.send(JSON.stringify(payload));

            // Optimistically add the message to local state
            setMessages(prev => [...prev, { ...payload, status: 'sending' }]);
            setMessageInput({ message: "" });

            setSnackBarState({
                open: true,
                msg: "Message sent!",
                severity: "success",
                vertical: "top",
                horizontal: "center",
            });
        } catch (error) {
            console.error("Error sending message:", error);
            setSnackBarState({
                open: true,
                msg: "Failed to send message",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
        }
    }, [messageInput, socketConnected]);

    const handleKeyPress = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <>
            <Grid container spacing={2} className={classes.gridContainer}>
                {/* Message display area */}
                <Grid item xs={12}>
                    <Box style={{
                        height: '200px',
                        overflowY: 'auto',
                        border: '1px solid #ccc',
                        padding: '10px',
                        marginBottom: '10px'
                    }}>
                        {messages.map((msg, index) => (
                            <div key={index} style={{
                                marginBottom: '8px',
                                padding: '5px',
                                backgroundColor: msg.status === 'sending' ? '#f5f5f5' : 'transparent',
                                display: 'flex',
                                alignItems: 'center'
                            }}>
                                {msg?.sender === sender ? (
                                    <>
                                        <AccountCircleIcon style={{ marginRight: 8, color: '#3f51b5' }} />
                                        <span style={{ fontWeight: 'bold' }}>You</span>
                                    </>
                                ) : (
                                    <>
                                        <AccountCircleIcon style={{ marginRight: 8, color: '#f50057' }} />
                                        <span style={{ fontWeight: 'bold' }}>{msg?.userSender?.username}</span>
                                    </>
                                )}
                                <span style={{ marginLeft: '16px' }}>
                                    <strong>{new Date(msg.timestamp).toLocaleTimeString()}:</strong> {msg.content}
                                </span>
                                {msg.status === 'sending' &&
                                    <span style={{ color: '#666', marginLeft: '5px' }}>(Sending...)</span>}
                            </div>
                        ))}
                    </Box>
                </Grid>

                {/* Message input */}
                <Grid item xs={12}>
                    <C1TextArea
                        label={t("job:Enter your message")}
                        name="message"
                        value={getValue(messageInput?.message)}
                        onChange={handleInputChange}
                        // onKeyPress={handleKeyPress}
                        multiline
                        textLimit={1024}
                    />
                </Grid>

                <Grid item xs={12} style={{ display: "flex", justifyContent: "flex-end" }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSendMessage}
                        disabled={!socketConnected || !messageInput.message?.trim()}
                        style={{ marginRight: "20px", color: "#fff" }}
                    >
                        {t("job:Send")}
                    </Button>

                    <Button
                        variant="outlined"
                        color="secondary"
                        onClick={handleOnClose}
                    >
                        {t("job:Close")}
                    </Button>
                </Grid>
            </Grid>

            <Snackbar
                anchorOrigin={{
                    vertical: snackBarState.vertical,
                    horizontal: snackBarState.horizontal,
                }}
                open={snackBarState.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackBar}
                key={snackBarState.vertical + snackBarState.horizontal}
            >
                <C1Alert onClose={handleCloseSnackBar} severity={snackBarState.severity}>
                    {snackBarState.msg}
                </C1Alert>
            </Snackbar>
        </>
    );
};

export default UserPopUpMessage;
