import { navigations } from "../../navigations";
import useAuth from "../../hooks/useAuth";

export const SET_USER_NAVIGATION = "SET_USER_NAVIGATION";


const getfilteredNavigations = (navList = [], role) => {
  console.log("role", role)
  return navList.reduce((array, nav) => {
    if (nav.auth) {
      if (nav.auth.includes(role)) {
        array.push(nav);
      }
    } else {
      if (nav.children) {
        nav.children = getfilteredNavigations(nav.children, role);
        array.push(nav);
      } else {
        array.push(nav);
      }
    }
    return array;
  }, []);
};

export function getNavigationByUser(user) {
  return (dispatch, getState) => {
    // Get current navigations from state
    const { navigations = [] } = getState().navigation || {};

    // Get user's role from the provided user object
    const role = user?.roles?.[0]?.name;

    console.log("user", user);
    console.log("role", role);

    // Filter navigations based on role
    const filteredNavigations = getfilteredNavigations(navigations, role);

    // Dispatch the filtered navigations
    dispatch({
      type: SET_USER_NAVIGATION,
      payload: filteredNavigations
    });
  };
}

/**Retrieves the menu from backend. */
export function getMenuByUser(user) {
  return (dispatch, getState) => {
    dispatch({
      type: SET_USER_NAVIGATION,
      payload: navigations
      // payload: navigations
    });

  };
}
