import React from "react";
import {
  Card,
  Grid,
  Button,
  Divider,
  Box,
} from "@material-ui/core";

import { makeStyles } from "@material-ui/core/styles";
import clsx from "clsx";
import { useHistory } from "react-router-dom";
import portEdiLogo from '../sessions/login/PORT EDI LOGO.JPG';

const useStyles = makeStyles(({ palette, ...theme }) => ({
  cardHolder: {
    background: "#3C77D0",
  },
  card: {
    maxWidth: 1000,
    borderRadius: 12,
    margin: "1rem",
  },
  buttonProgress: {
    position: "absolute",
    top: "50%",
    left: "50%",
    marginTop: -12,
    marginLeft: -12,
  },
}));

const NoPermission = () => {

  const classes = useStyles();
  const history = useHistory();
  return (
    <Box
      className={clsx(
        "flex justify-center items-center  min-h-full-screen",
        classes.cardHolder
      )}
    >
      <Card className={classes.card}>
        <Grid container>
          <Grid item lg={5} md={5} sm={5} xs={12}>
            <Box className="p-8 flex justify-center items-center h-full">
              <img
                className="w-200"
                src={portEdiLogo}
                alt=""
              />
            </Box>
          </Grid>
          <Grid item lg={7} md={7} sm={7} xs={12}>
            <Box className="p-8 h-full bg-light-gray relative">
              <Box className="m-2" fontWeight='fontWeightMedium'>Unauthorized Access</Box>
              <Divider />
              <br />
              <Box className="m-2">
                You are not authorized to access this page.
                <br />
                <br />
                <Button variant="contained" color="primary"
                  onClick={() => history.goBack()}> Back </Button>
              </Box>
              <br />
              <br />
              <br />
              <br />
            </Box>
          </Grid>
        </Grid>
      </Card>
    </Box >
  );
};

export default NoPermission;
