import { useEffect, useRef, useState } from 'react';
import { Client } from '@stomp/stompjs'; // Assuming you're using @stomp/stompjs for STOMP

const PrivateChatPopUpMessage = () => {
    const [isConnected, setIsConnected] = useState(false);
    const [messages, setMessages] = useState([]);
    const stompClientRef = useRef(null);
    const username = 'userA'; // Assuming the username comes from somewhere
    const popUpUsername = 'userB'; // Assuming the username comes from somewhere

    useEffect(() => {
        if (!username) return;

        const token = localStorage.getItem('accessToken');

        // WebSocket connection using SockJS
        const socket = new SockJS('http://localhost:8080/ws'); // This is where you connect

        // Creating the STOMP client
        const stompClient = new Client({
            webSocketFactory: () => {
                socket.onopen = () => {
                    // Injecting headers on WebSocket connection
                    socket.send('Authorization', `Bearer ${token}`);
                };
                return socket;
            },
            connectHeaders: {
                Authorization: `Bearer ${token}`, // Make sure header is included
            },
            reconnectDelay: 5000,
            onConnect: () => {
                console.log('✅ Connected');
                setIsConnected(true); // Track connection status

                // Subscribe to the chat messages
                stompClient.subscribe(`/user/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages((prev) => [...prev, msg]);
                });
            },
            onStompError: (frame) => {
                console.error('❌ STOMP Error', frame);
            },
        });

        // Activate the client
        stompClient.activate();
        stompClientRef.current = stompClient;

        // Cleanup on component unmount
        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username, popUpUsername]);

    return (
        <div>
            {isConnected && <h2>Connected to the WebSocket!</h2>}
            <div>
                {messages.map((message, index) => (
                    <div key={index}>
                        <strong>{message.sender}</strong>: {message.content}
                    </div>
                ))}
            </div>
        </div>
    );
};

export default PrivateChatPopUpMessage;
