import React, { useEffect, useRef, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import '../../styles/privateChatPopUpMessage.css'; // Updated import path

const PrivateChatPopUpMessage = () => {
    const [isConnected, setIsConnected] = useState(false);
    const [messages, setMessages] = useState([]);
    const stompClientRef = useRef(null);
    const username = 'userA'; // Assuming the username comes from somewhere
    const popUpUsername = 'userB'; // Assuming the username comes from somewhere

    useEffect(() => {
        if (!username) return;

        const token = localStorage.getItem('accessToken');

        // WebSocket connection using SockJS
        const socket = new SockJS(`http://localhost:8080/ws?token=${token}`);

        // Creating the STOMP client
        const stompClient = new Client({
            webSocketFactory: () => {
                socket.onopen = () => {
                    // Injecting headers on WebSocket connection
                    socket.send('Authorization', `Bearer ${token}`);
                };
                return socket;
            },
            connectHeaders: {
                Authorization: `Bearer ${token}`, // Make sure header is included
            },
            reconnectDelay: 5000,
            onConnect: () => {
                console.log('✅ Connected');
                setIsConnected(true); // Track connection status

                // Subscribe to the chat messages
                stompClient.subscribe(`/user/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages((prev) => [...prev, msg]);
                });

                // Example of sending a message
                stompClient.publish({
                    destination: "/app/private-message",
                    body: JSON.stringify({
                        sender: username,
                        receiver: popUpUsername,
                        content: "Hello!"
                    })
                });
            },
            onStompError: (frame) => {
                console.error('❌ STOMP Error', frame);
            },
        });

        // Activate the client
        stompClient.activate();
        stompClientRef.current = stompClient;

        // Cleanup on component unmount
        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username, popUpUsername]);

    return (
        <div className="chat-container">
            {isConnected && <div className="status-bar">Connected</div>}
            <div className="messages-container">
                {messages.map((message, index) => (
                    <div
                        key={index}
                        className={`message ${message.sender === username ? 'sent' : 'received'}`}
                    >
                        <div className="message-header">
                            <strong>{message.sender}</strong>
                            <span className="message-time">{new Date().toLocaleTimeString()}</span>
                        </div>
                        <div className="message-content">{message.content}</div>
                    </div>
                ))}
            </div>
            <div className="message-input">
                <input type="text" placeholder="Type a message..." />
                <button>Send</button>
            </div>
        </div>
    );
};

export default PrivateChatPopUpMessage;
