import React, {useEffect, useState} from "react";
import {useTranslation} from "react-i18next";
import Grid from "@material-ui/core/Grid";
import DescriptionIcon from "@material-ui/icons/DescriptionOutlined";
import {Box, Button, MenuItem} from "@material-ui/core";
import Snackbar from "@material-ui/core/Snackbar";
import {useStyles} from "../../../c1utils/styles";
import C1InputField from "../../../c1component/C1InputField";
import {MatxLoading} from "../../../../matx";
import C1SelectField from "../../../c1component/C1SelectField";
import useHttp from "../../../c1hooks/http";
import C1TextArea from "../../../c1component/C1TextArea";
import C1Alert from "../../../c1component/C1Alert";

const UserPopUpMessage = (prop) => {
    const {
        errors,
        cargoData,
        cargoTypes,
        isDisabled,
        setCargoData,
        handleSave,
        notEditable,
        handleOnClose,
        handleInputCargo,
        ableToModifiesCargo,
        handleAutoComplete
    } = prop;

    const classes = useStyles();
    const { t } = useTranslation(['job']);
    const { isLoading, isFormSubmission, res, validation, error, urlId, sendRequest } = useHttp();
    const [isSubmitSuccess, setSubmitSuccess] = useState(false);
    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: 'top',
        horizontal: 'center',
        msg: 'success',
        severity: 'success'
    });


    const handleClose = () => {
        setSnackBarState({ ...snackBarState, open: false });
    };
    let snackBar;
    if (isSubmitSuccess) {
        const anchorOriginV = snackBarState.vertical;
        const anchorOriginH = snackBarState.horizontal;

        snackBar = <Snackbar
            anchorOrigin={{ vertical: anchorOriginV, horizontal: anchorOriginH }}
            open={snackBarState.open}
            onClose={handleClose}
            autoHideDuration={3000}
            key={anchorOriginV + anchorOriginH
            }>
            <C1Alert onClose={handleClose} severity={snackBarState.severity}>
                {snackBarState.msg}
            </C1Alert>
        </Snackbar>;
    }
    return (
        <React.Fragment>
            {isLoading && <MatxLoading />}
            {snackBar}
            <Grid container alignItems="stretch" spacing={2} className={classes.gridContainer}>

                <div style={{display: 'flex', justifyContent: 'flex-end', width: '100%', margin: '20px'}}>
                    {notEditable || (ableToModifiesCargo ? !ableToModifiesCargo : isDisabled) ? (

                            <Button
                                variant="contained"
                                color="default"
                                onClick={handleOnClose}
                                style={{color: 'white', backgroundColor: 'orange'}}
                            >
                                Cancel
                            </Button>

                    ) : (
                        <>
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={handleSave}
                            style={{marginRight: '20px', color: 'white', backgroundColor: '#3d5afe'}}
                        >
                            Save
                        </Button>
                        <Button
                        variant="contained"
                        color="default"
                        onClick={handleOnClose}
                    style={{color: 'white', backgroundColor: 'orange'}}
                >
                    Cancel
                </Button>
                        </>
                )}
                </div>
            </Grid>
        </React.Fragment>
    );
};

export default UserPopUpMessage;
