import React, { useEffect, useRef, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import './PrivateChatPopUpMessage.css';

const PrivateChatPopUpMessage = () => {
    const [isConnected, setIsConnected] = useState(false);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const stompClientRef = useRef(null);
    const messagesEndRef = useRef(null);
    const username = 'userA'; // Replace with dynamic username
    const popUpUsername = 'userB'; // Replace with dynamic username

    const scrollToBottom = () => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    };

    useEffect(() => {
        scrollToBottom();
    }, [messages]);

    useEffect(() => {
        if (!username) return;

        const token = localStorage.getItem('accessToken');

        // WebSocket connection using SockJS
        const socket = new SockJS(`http://localhost:8080/ws?token=${token}`);

        // Creating the STOMP client
        const stompClient = new Client({
            webSocketFactory: () => socket, // Simply return the socket
            connectHeaders: {
                Authorization: `Bearer ${token}`,
            },
            reconnectDelay: 5000,
            onConnect: () => {
                console.log('✅ Connected');
                setIsConnected(true);

                stompClient.subscribe(`/user/queue/messages`, (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages((prev) => [...prev, msg]);
                });

                stompClient.publish({
                    destination: '/app/private-message',
                    body: JSON.stringify({
                        sender: username,
                        receiver: popUpUsername,
                        content: 'Hello!',
                    }),
                });
            },
            onStompError: (frame) => {
                console.error('❌ STOMP Error', frame);
            },
        });

        stompClient.activate();
        stompClientRef.current = stompClient;

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username, popUpUsername]);

    const handleSendMessage = (e) => {
        e.preventDefault();
        if (newMessage.trim() && stompClientRef.current && isConnected) {
            const message = {
                sender: username,
                receiver: popUpUsername,
                content: newMessage,
            };
            stompClientRef.current.publish({
                destination: '/app/private-message',
                body: JSON.stringify(message),
            });
            setMessages((prev) => [...prev, message]);
            setNewMessage('');
        }
    };

    return (
        <div className="chat-container">
            <div className="chat-header">
                <h2>Chat with {popUpUsername}</h2>
                <span className={`status ${isConnected ? 'connected' : 'disconnected'}`}>
                    {isConnected ? 'Connected' : 'Disconnected'}
                </span>
            </div>
            <div className="messages-container">
                {messages.map((message, index) => (
                    <div
                        key={index}
                        className={`message ${
                            message.sender === username ? 'sent' : 'received'
                        }`}
                    >
                        <div className="message-content">
                            <strong>{message.sender}</strong>
                            <p>{message.content}</p>
                        </div>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>
            <form className="message-input" onSubmit={handleSendMessage}>
                <input
                    type="text"
                    value={newMessage}
                    onChange={(e) => setNewMessage(e.target.value)}
                    placeholder="Type a message..."
                    disabled={!isConnected}
                />
                <button type="submit" disabled={!isConnected}>
                    Send
                </button>
            </form>
        </div>
    );
};

export default PrivateChatPopUpMessage;
