import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { Client } from "@stomp/stompjs";

const UserPopUpMessage = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const stompClientRef = useRef(null);

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        const client = new Client({
            webSocketFactory: () => socket,
            reconnectDelay: 5000,
            onConnect: () => {
                console.log("Connected");

                client.subscribe("/topic/public", (message) => {
                    const msg = JSON.parse(message.body);
                    setMessages((prev) => [...prev, msg]);
                });
            }
        });

        client.activate();
        stompClientRef.current = client;

        return () => client.deactivate();
    }, []);

    const sendMessage = () => {
        const client = stompClientRef.current;
        if (client && input.trim()) {
            const message = {
                sender: "User",
                content: input,
                type: "CHAT"
            };
            client.publish({ destination: "/app/chat.sendMessage", body: JSON.stringify(message) });
            setInput("");
        }
    };

    return (
        <div>
            <h2>WebSocket Chat</h2>
            <div style={{ border: "1px solid gray", height: 200, overflowY: "scroll", padding: 10 }}>
                {messages.map((m, idx) => (
                    <div key={idx}><strong>{m.sender}:</strong> {m.content}</div>
                ))}
            </div>
            <input
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                placeholder="Type a message..."
            />
            <button onClick={sendMessage}>Send</button>
        </div>
    );
};

export default UserPopUpMessage;
