import React, { useEffect, useRef, useState } from 'react';
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
import { Avatar, Grid, TextField, Typography, IconButton, Paper } from '@mui/material';
import SendIcon from '@mui/icons-material/Send';
import PersonIcon from '@mui/icons-material/Person';
import useAuth from "../../../hooks/useAuth";

// Function to refresh access token
const refreshAccessToken = async (refreshToken) => {
    console.log("refreshToken", refreshToken)
    const response = await fetch('http://localhost:8080/refresh-token', {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({ refreshToken }),
    });

    if (response.ok) {
        const data = await response.json();
        console.log("data", data)
        localStorage.setItem('accessToken', data.accessToken);
        localStorage.setItem('accessTokenExpiry', data.expiry); // Store expiry time
        return data.accessToken;
    } else {
        console.error('Failed to refresh access token');
        return null;
    }
};

const getValidToken = async () => {
    const accessToken = localStorage.getItem('accessToken');
    const refreshToken = localStorage.getItem('refreshToken');
    const expiry = localStorage.getItem('accessTokenExpiry');
    const currentTime = new Date().getTime() / 1000; // Current time in seconds

    // Log the expiration time
    console.log('Access token expired', expiry);
    console.log('refreshToken', refreshToken);

    // If there's no expiry or token is expired, try refreshing
    if (accessToken && (expiry === null || currentTime > expiry)) {
        console.log('Access token expired or no expiry found, trying to refresh it...');
        const newToken = await refreshAccessToken(refreshToken);
        console.log("newToken", newToken)

        // If refresh is successful, store the new access token and expiry
        if (newToken) {
            localStorage.setItem('accessToken', newToken.token);
            localStorage.setItem('accessTokenExpiry', newToken.expiredAt);
            return newToken.token;
        }
    }

    return accessToken; // Return the current access token if not expired or refreshing failed
};


const PrivateChatPopUpMessage = ({ popUpUsername }) => {
    const authUser = useAuth();
    const username = authUser?.user?.username;
    const [messages, setMessages] = useState([]);
    const [isConnected, setIsConnected] = useState(false);
    const [input, setInput] = useState('');
    const stompClientRef = useRef(null);

    useEffect(() => {
        console.log("Messages state updated:", messages);
    }, [messages]);

    useEffect(() => {
        if (!username) return;

        const connectWebSocket = async () => {
            const token = await getValidToken(); // ✅ await here

            if (!token) {
                console.error('❌ No valid token available');
                return;
            }

            const socket = new SockJS(`http://localhost:8080/ws?token=${token}`);

            const stompClient = new Client({
                webSocketFactory: () => socket,
                connectHeaders: {
                    Authorization: `Bearer ${token}`,
                },
                reconnectDelay: 5000,
                onConnect: () => {
                    console.log('✅ Connected');
                    setIsConnected(true);

                    stompClient.subscribe(`/user/${username}/queue/messages`, (message) => {
                        const msg = JSON.parse(message.body);
                        setMessages((prev) => [...prev, msg]);
                    });

                    stompClient.publish({
                        destination: '/app/chat.getMessages',
                        body: JSON.stringify({
                            sender: username,
                            receiver: popUpUsername,
                        }),
                    });
                },
                onStompError: (frame) => {
                    console.error('❌ STOMP Error', frame);
                },
            });

            stompClient.activate();
            stompClientRef.current = stompClient;
        };

        connectWebSocket(); // 🚀 Launch the async function

        return () => {
            if (stompClientRef.current) {
                stompClientRef.current.deactivate();
            }
        };
    }, [username, popUpUsername]);
    const sendMessage = () => {
        if (!input.trim() || !popUpUsername || !stompClientRef.current?.connected) return;

        const message = {
            sender: username,
            receiver: popUpUsername,
            content: input,
            type: 'CHAT',
        };

        stompClientRef.current.publish({
            destination: '/app/chat.sendPrivate',
            body: JSON.stringify(message),
        });

        setMessages((prev) => [...prev, message]);
        setInput('');
    };

    return (
        <Grid container direction="column" spacing={3} style={{ padding: 30 }}>
            <Grid item>
                <Typography variant="h6">Chat with {popUpUsername}</Typography>
            </Grid>

            <Grid item style={{ height: 300, overflowY: 'auto', border: '1px solid #ddd', borderRadius: 8, padding: 8 }}>
                {messages.map((msg, idx) => {
                    const isSender = msg.sender === username;
                    return (
                        <Grid
                            container
                            key={idx}
                            justifyContent={isSender ? 'flex-end' : 'flex-start'}
                            alignItems="center"
                            spacing={1}
                            style={{ marginBottom: 8 }}
                        >
                            {!isSender && (
                                <Grid item>
                                    <Avatar>
                                        <PersonIcon />
                                    </Avatar>
                                </Grid>
                            )}
                            <Grid item xs={3}>
                                <Paper
                                    elevation={2}
                                    style={{
                                        padding: '8px 12px',
                                        backgroundColor: isSender ? '#e0f7fa' : '#f1f8e9',
                                        borderRadius: 12,
                                    }}
                                >
                                    <Typography variant="body2" color="textSecondary">
                                        {msg.sender}
                                    </Typography>
                                    <Typography>{msg.content}</Typography>
                                </Paper>
                            </Grid>
                            {isSender && (
                                <Grid item>
                                    <Avatar style={{ backgroundColor: '#00796b' }}>
                                        <PersonIcon />
                                    </Avatar>
                                </Grid>
                            )}
                        </Grid>
                    );
                })}
            </Grid>

            <Grid item>
                <Grid container spacing={1} alignItems="center">
                    <Grid item xs={10}>
                        <TextField
                            fullWidth
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            placeholder="Type a message..."
                            variant="outlined"
                            size="small"
                        />
                    </Grid>
                    <Grid item xs={2}>
                        <IconButton onClick={sendMessage} color="primary">
                            <SendIcon />
                        </IconButton>
                    </Grid>
                </Grid>
            </Grid>
        </Grid>
    );
};

export default PrivateChatPopUpMessage;
