import React, { useEffect, useRef, useState, useCallback } from "react";
import { useTranslation } from "react-i18next";
import Grid from "@material-ui/core/Grid";
import { Box, Button, Snackbar } from "@material-ui/core";
import { useStyles } from "../../../c1utils/styles";
import C1TextArea from "../../../c1component/C1TextArea";
import C1Alert from "../../../c1component/C1Alert";
import {deepUpdateState} from "../../../c1utils/stateUtils";

const UserPopUpMessage = ({ handleOnClose }) => {
    const classes = useStyles();
    const { t } = useTranslation(["job"]);

    const socketRef = useRef(null);
    const [messageInput, setMessageInput] = useState(""); // For the current input
    const [messages, setMessages] = useState([]); // For all messages
    const [socketConnected, setSocketConnected] = useState(false);

    const [snackBarState, setSnackBarState] = useState({
        open: false,
        vertical: "top",
        horizontal: "center",
        msg: "",
        severity: "success",
    });

    const handleCloseSnackBar = () => {
        setSnackBarState(prev => ({ ...prev, open: false }));
    };

    // WebSocket connection management
    useEffect(() => {
        const socket = new WebSocket('ws://localhost:8080/ws');
        socketRef.current = socket;

        const handleOpen = () => {
            console.log('WebSocket connected');
            setSocketConnected(true);
            setSnackBarState({
                open: true,
                msg: "Connected to chat server",
                severity: "success",
                vertical: "top",
                horizontal: "center",
            });
        };

        const handleError = (error) => {
            console.error("WebSocket error", error);
            setSocketConnected(false);
            setSnackBarState({
                open: true,
                msg: "Connection error. Trying to reconnect...",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
        };

        const handleClose = () => {
            console.log("WebSocket closed");
            setSocketConnected(false);
        };

        const handleMessage = (event) => {
            try {
                const response = JSON.parse(event.data);
                console.log("Received message:", response);
                setMessages(prev => [...prev, response]);
            } catch (error) {
                console.error("Error parsing message:", error);
            }
        };

        socket.addEventListener('open', handleOpen);
        socket.addEventListener('error', handleError);
        socket.addEventListener('close', handleClose);
        socket.addEventListener('message', handleMessage);

        return () => {
            socket.removeEventListener('open', handleOpen);
            socket.removeEventListener('error', handleError);
            socket.removeEventListener('close', handleClose);
            socket.removeEventListener('message', handleMessage);

            if (socket.readyState === WebSocket.OPEN) {
                socket.close();
            }
        };
    }, []);

    const handleSendMessage = useCallback(() => {
        if (!messageInput.trim()) {
            setSnackBarState({
                open: true,
                msg: "Message cannot be empty",
                severity: "warning",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        if (!socketConnected) {
            setSnackBarState({
                open: true,
                msg: "Not connected to server",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
            return;
        }

        const payload = {
            type: "MESSAGE",
            content: messageInput,
            timestamp: new Date().toISOString(),
        };

        try {
            socketRef.current.send(JSON.stringify(payload));

            // Optimistically add the message to local state
            setMessages(prev => [...prev, { ...payload, status: 'sending' }]);
            setMessageInput("");

            setSnackBarState({
                open: true,
                msg: "Message sent!",
                severity: "success",
                vertical: "top",
                horizontal: "center",
            });
        } catch (error) {
            console.error("Error sending message:", error);
            setSnackBarState({
                open: true,
                msg: "Failed to send message",
                severity: "error",
                vertical: "top",
                horizontal: "center",
            });
        }
    }, [messageInput, socketConnected]);

    const handleKeyPress = (e) => {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault();
            handleSendMessage();
        }
    };

    const handleInpuChange = (e) => {
        let elName = e.target.name;
        let elSelectedValue = e.target.value;
        setMessageInput({ ...messageInput, ...deepUpdateState(messageInput, elName, elSelectedValue) });
    };
    return (
        <>
            <Grid container spacing={2} className={classes.gridContainer}>
                {/* Message display area */}
                <Grid item xs={12}>
                    <Box style={{
                        height: '200px',
                        overflowY: 'auto',
                        border: '1px solid #ccc',
                        padding: '10px',
                        marginBottom: '10px'
                    }}>
                        {messages.map((msg, index) => (
                            <div key={index} style={{
                                marginBottom: '8px',
                                padding: '5px',
                                backgroundColor: msg.status === 'sending' ? '#f5f5f5' : 'transparent'
                            }}>
                                <strong>{msg.timestamp}:</strong> {msg.content}
                                {msg.status === 'sending' &&
                                    <span style={{ color: '#666', marginLeft: '5px' }}>(Sending...)</span>}
                            </div>
                        ))}
                    </Box>
                </Grid>

                {/* Message input */}
                <Grid item xs={12}>
                    <C1TextArea
                        label={t("job:Enter your message")}
                        name="message"
                        value={messageInput}
                        onChange={handleInpuChange}
                        onKeyPress={handleKeyPress}
                        rows={4}
                        fullWidth
                    />
                </Grid>

                <Grid item xs={12} style={{ display: "flex", justifyContent: "flex-end" }}>
                    <Button
                        variant="contained"
                        color="primary"
                        onClick={handleSendMessage}
                        disabled={!socketConnected || !messageInput.trim()}
                        style={{ marginRight: "20px", color: "#fff" }}
                    >
                        {t("job:Send")}
                    </Button>

                    <Button
                        variant="outlined"
                        color="secondary"
                        onClick={handleOnClose}
                    >
                        {t("job:Close")}
                    </Button>
                </Grid>
            </Grid>

            <Snackbar
                anchorOrigin={{
                    vertical: snackBarState.vertical,
                    horizontal: snackBarState.horizontal,
                }}
                open={snackBarState.open}
                autoHideDuration={3000}
                onClose={handleCloseSnackBar}
                key={snackBarState.vertical + snackBarState.horizontal}
            >
                <C1Alert onClose={handleCloseSnackBar} severity={snackBarState.severity}>
                    {snackBarState.msg}
                </C1Alert>
            </Snackbar>
        </>
    );
};

export default UserPopUpMessage;
