import React, { useEffect, useState, useRef } from "react";
import SockJS from "sockjs-client";
import { over } from "stompjs";

const UserPopUpMessage = () => {
    const [messages, setMessages] = useState([]);
    const [input, setInput] = useState("");
    const stompClient = useRef(null);

    useEffect(() => {
        const socket = new SockJS("http://localhost:8080/ws");
        stompClient.current = over(socket);

        stompClient.current.connect({}, () => {
            console.log("ðŸŸ¢ Connected to WebSocket");

            stompClient.current.subscribe("/topic/public", (msg) => {
                const payload = JSON.parse(msg.body);
                setMessages(prev => [...prev, payload]);
            });
        });

        return () => {
            stompClient.current.disconnect(() => {
                console.log("ðŸ”´ Disconnected");
            });
        };
    }, []);

    const sendMessage = () => {
        if (input && stompClient.current) {
            const chatMessage = {
                sender: "User",
                content: input
            };
            stompClient.current.send("/app/chat.sendMessage", {}, JSON.stringify(chatMessage));
            setInput("");
        }
    };

    return (
        <div style={{ padding: "20px", maxWidth: "500px", margin: "auto" }}>
            <h2>WebSocket Chat</h2>
            <div style={{ border: "1px solid gray", height: "300px", overflowY: "scroll", padding: "10px" }}>
                {messages.map((msg, idx) => (
                    <div key={idx}><strong>{msg.sender}:</strong> {msg.content}</div>
                ))}
            </div>
            <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                placeholder="Type your message"
                style={{ width: "80%", padding: "8px" }}
            />
            <button onClick={sendMessage} style={{ padding: "8px 16px", marginLeft: "8px" }}>
                Send
            </button>
        </div>
    );
};

export default UserPopUpMessage;
